import { withOktaAuth } from '@okta/okta-react/bundles/types'
import React, { Component } from 'react'
import JobList from './JobList'


class Home extends Component {
  state = {
    jobs: []
  }

  async componentDidMount() {
    const page = this.props.match.params.page
    getAllJobs = async (page, size) => {
      API.get(`jobs?page=${page}&size=${size}`, {
        headers: {
          'Authorization': 'Bearer ' + await this.props.authState.accessToken.accessToken
        }
      })
        .then(response => {
          const { content, first, last, number, size, totalElements, totalPages } = response.data
          this.setState({
            jobs: content,
            pagination: {
              first,
              last,
              number,
              size,
              totalElements,
              totalPages
            }
          })
        })
        .catch(error => {
          console.log(error)
          M.toast({ html: error, classes: 'rounded' })
        })
    }
  }

  render() {
    const { jobs } = this.state

    return (
      <div>
        {/* <SideBar/> */}
        <div className="parallax-container">
          <div className="parallax"><img src="/nyc.jpg" alt="" /></div>
        </div>
        <div className="section white">
          <div className="container">
            <JobList jobs={jobs} />
          </div>
        </div>
      </div>
    )
  }
}

export default withOktaAuth(Home)
