import React, { Component } from 'react'
import { withOktaAuth } from '@okta/okta-react'
import API from '../misc/api'
import { Link, useParams } from 'react-router-dom';

class ProductForm extends Component {
    render() {
        const showCode = (this.props.match.path === "/add-product") ? { "display": "block" } : { "display": "none" }
        
        return (
            <form class="w3-container" method="post" style={{margin: "250px"}}>
                <div  style={showCode}>
                    <label class="w3-text-black w3-xlarge"><b>Code </b></label>
                    <input class="w3-input w3-border w3-white" name="name" type="text" required />
                </div>
                <div>
                    <label class="w3-text-black w3-xlarge"><b>Name </b></label>
                    <input class="w3-input w3-border w3-white" name="name" type="text" required />
                </div>
                <div>
                    <label class="w3-text-black w3-xlarge"><b>Description</b></label>
                    <input class="w3-input w3-border w3-white" name="description" type="text" required />
                </div>
                <button class="w3-btn w3-teal w3-large w3-bordered w3-margin" >Submit</button>

            </form>
        )
    }
}

export default withOktaAuth(ProductForm)
