import React, { Component } from 'react'
import { withOktaAuth } from '@okta/okta-react'
import API from '../misc/api'
import ProductList from './ProductList'
import DeleteDialog from '../staff/DeleteDialog'
import M from 'materialize-css'
import { Link, useParams } from 'react-router-dom'
import qs from 'qs';


class Product extends Component {
    state = {
        product: [],
        code: ''
    }

    deleteModal = null


    showDialog = (code) => {
        this.code = code
        this.deleteModal.open()
    }

    async componentDidMount() {

        this.getProducts()
        this.deleteModal = M.Modal.init(document.getElementById('deleteModal'))
    }
    getProducts = async (page) => {
        API.get(`products/1`, {
            headers: {
                'Authorization': 'Bearer ' + await this.props.oktaAuth.authStateManager._authState.accessToken.accessToken
            }
        })
            .then(response => {

                const pageNum = new URLSearchParams(window.location.search).get('page');
                const keyword = new URLSearchParams(window.location.search).get('keyword');
                if (pageNum == null) {
                    pageNum = 1;
                }
                let pageData = new Object()
                if (keyword != null) {
                    const filter = response.data.filter(item =>
                        item.name.toLowerCase().includes(keyword)
                    )
                    pageData = filter.slice((pageNum * 2) - 2, pageNum * 2)
                } else {
                    // const pageData = response.data.slice((pageNum * 2) - 2, pageNum * 2)
                    pageData = response.data.slice(0, 1)
                }
                console.log(this.props.oktaAuth.authStateManager._authState.accessToken.accessToken)
                this.setState({
                    product: pageData
                })
            })
            .catch(error => {
                console.log(error)
            })
    }

    deleteProduct = async () => {
        window.location.reload(false);
        API.delete(`products/${this.code}`, {
            headers: {
                'Authorization': 'Bearer ' + await this.props.authState.accessToken.accessToken
            }
        })
            .then(() => {

            })
            .catch(error => {
                console.log(error)
            })
    }

    render() {
        return (
            <div>
                <ProductList products={this.state.product} showDialog={this.showDialog} />

                <DeleteDialog deleteObject={this.deleteProduct} />
            </div>
        )
    }
}

export default withOktaAuth(Product)
