import React, { useState, useEffect } from "react"
import { Route } from 'react-router-dom'
import { OktaAuth, toRelativeUrl } from '@okta/okta-auth-js'
import { Security, SecureRoute, LoginCallback } from '@okta/okta-react'
import { useHistory } from 'react-router-dom';
import Navbar from './components/common/Navbar'
import Home from './components/home/Home'
import Product from "./components/product/Product";

function App() {
  const [accessToken, setToken] = useState(``)

  const oktaAuth = new OktaAuth({
    issuer: `https://dev-27044634.okta.com/oauth2/default`,
    clientId: `0oa7xj2om73nhXae15d7`,
    redirectUri: `${window.location.origin}/implicit/callback`
  })

  const history = useHistory();
  const restoreOriginalUri = async (_oktaAuth, originalUri) => {
    setToken(oktaAuth.authStateManager._authState.accessToken.accessToken);
    history.replace(toRelativeUrl(originalUri || '/', window.location.origin));
  };

  return (
    <Security oktaAuth={oktaAuth} restoreOriginalUri={restoreOriginalUri} >
      <div className="App"> 
        <Navbar />
        <Route path='/' exact component={Home} />
        <Route path='/login' exact component={Home} />
        <SecureRoute path='/products' exact component={Product(accessToken)} />
        <Route path='/implicit/callback' component={LoginCallback} />
      </div>
    </Security>
  )
}

export default App
