import React, { Component } from 'react'
import { withOktaAuth } from '@okta/okta-react'
import API from '../misc/api'
import { Link } from 'react-router-dom'
import ProductList from './ProductList'


class Product extends Component {
    state = {
        product: null
    }

    async componentDidMount() {
        this.getProducts(1)
    }
    getProducts = async (page) => {
        API.get(`products/1`, {
            headers: {
                'Authorization': 'Bearer ' + await this.props.oktaAuth.authStateManager._authState.accessToken.accessToken
            }
        })
            .then(response => {
                console.log(response.data)
                this.setState({
                    product: response.data
                })
            })
            .catch(error => {
                console.log(error)
            })
    }

    render() {
        const { products } = this.state
        const productList = products && (
            <tr>
                <th>
                    {products.code}
                </th>
                <th>
                    {products.name}
                </th>
                <th>
                    {products.description}
                </th>
                <th>
                    <Link exact to={'/updateproduct/' + products.code} key={products.code}> Update
                    </Link>
                </th>
                <th></th>

            </tr>
        )
        return (
            <div className='content'>
                <div className='w3-container' style={{ margin: "200px" }}>
                    <div class="w3-responsive">
                        <table class="w3-table w3-striped w3-bordered w3-xlarge">
                            <tr>
                                <th>
                                    Code
                                </th>
                                <th>
                                    Name
                                </th>
                                <th>
                                    Description
                                </th>
                                <th></th>
                                <th></th>
                            </tr>
                            <ProductList products = {products}></ProductList>
                        </table>
                    </div>
                </div>
            </div>
        )
    }
}

export default withOktaAuth(Product)
