import React, { Component } from 'react'
import { withOktaAuth } from '@okta/okta-react'
import API from '../misc/api'
import ProductList from './ProductList'
import DeleteModal from '../common/DeleteModal'


class Product extends Component {
    state = {
        product: []
    }

    deleteModal = null

    componentDidMount() {
        this.deleteModal = document.getElementById('deleteModal')
    }
  
    showDialog = (name) => {
        console.log(document.getElementById('deleteModal'))
    }

    deleteProduct = async (id) => {
        API.delete(`products/${id}`, {
          headers: {
            'Authorization': 'Bearer ' + await this.props.authState.accessToken.accessToken
          }
        })
          .then(() => {
            
          })
          .catch(error => {
            console.log(error)
          })
      }

    async componentDidMount() {
        this.getProducts(1)
    }
    getProducts = async (page) => {
        API.get(`products/1`, {
            headers: {
                'Authorization': 'Bearer ' + await this.props.oktaAuth.authStateManager._authState.accessToken.accessToken
            }
        })
            .then(response => {
                console.log(response.data[0])
                this.setState({
                    product: response.data
                })
            })
            .catch(error => {
                console.log(error)
            })
    }



    render() {
        return (
            <div>
                 <ProductList products={this.state.product} showDialog={this.showDialog} />
                 <DeleteModal deleteFunction={this.deleteProduct}/>
            </div>
        )
    }
}

export default withOktaAuth(Product)
