import React, { Component } from 'react'
import { withOktaAuth } from '@okta/okta-react'
import API from '../misc/api'
import ProductList from './ProductList'
import DeleteDialog from '../staff/DeleteDialog'
import M from 'materialize-css'
import { Link, useParams } from 'react-router-dom'
import qs from 'qs';
import Pagination from '../common/Pagination'


class Product extends Component {
    state = {
        product: [],
        code: '',
        page: 1,
        totalPage: 1
    }

    deleteModal = null


    showDialog = (code) => {
        this.code = code
        this.deleteModal.open()
    }

    async componentDidMount() {

        this.getProducts()
        this.deleteModal = M.Modal.init(document.getElementById('deleteModal'))
    }
    getProducts = async (page) => {
        API.get(`products/1`, {
            headers: {
                'Authorization': 'Bearer ' + await this.props.oktaAuth.authStateManager._authState.accessToken.accessToken
            }
        })
            .then(response => {

                let pageNum = new URLSearchParams(window.location.search).get('page');
                let keyword = new URLSearchParams(window.location.search).get('keyword');
                if (pageNum == null) {
                    pageNum = 1;
                }
                let pageData = new Object()
                if (keyword != null) {
                    let filter = response.data.filter(item =>
                        item.name.toLowerCase().includes(keyword)
                    )
                    pageData = filter.slice((pageNum * 2) - 2, pageNum * 2)
                } else {
                    pageData = response.data.slice((pageNum * 2) - 2, pageNum * 2)
                }
                console.log(this.props.oktaAuth.authStateManager._authState.accessToken.accessToken)
                this.setState({
                    product: pageData,
                    page: pageNum,
                    totalPage: (pageData.length / 3) + 1
                })
            })
            .catch(error => {
                console.log(error)
            })
    }

    deleteProduct = async () => {
        window.location.reload(false);
        API.delete(`products/${this.code}`, {
            headers: {
                'Authorization': 'Bearer ' + await this.props.authState.accessToken.accessToken
            }
        })
            .then(() => {

            })
            .catch(error => {
                console.log(error)
            })
    }

    render() {
        const previousPage = this.state.page - 1
        const nextPage =  this.state.page  + 1
        const minPage =  this.state.page  > 1 ? { "display": "block" } : { "display": "none" }
        const maxPage =  this.state.page  ==  this.state.totalPage ? { "display": "block" } : { "display": "none" }
        const url = window.location.pathname
        console.log(url)
        return (
            <div>
                <ProductList products={this.state.product} showDialog={this.showDialog} />
                <div class="w3-bar w3-large w3-middle">
                    <Link exactclassName='w3-button' style={minPage}>{previousPage}</Link>
                    <Link className='w3-button w3-green'>{ this.state.page }</Link>
                    <Link className='w3-button' style={maxPage}>{nextPage}</Link>
                </div>
                <DeleteDialog deleteObject={this.deleteProduct} />
            </div>
        )
    }
}

export default withOktaAuth(Product)
